import React, {Component} from 'react';

class Timeline extends Component {
  state = {
    title: "",
    rows: [],
    item:"",
  }

  handleChange = (event) => {
    let name = event.target.name;
    let value = event.target.value;
    this.setState({
      [name]: value
    });
  }

  add = (event) => {
    event.preventDefault();
    if(this.state.item){
      let rows = this.state.rows;
      let row = {
        item: this.state.item,
      }
      rows.push(row);
      this.setState({rows, item:""});
    }
  }

  delete = (index) => {
    let rows = this.state.rows;
    rows.splice(index, 1);
    this.setState({rows});
  }

  render(){
    let rows = this.state.rows.map((row,index) =>
      <li key={index} className="list-group-item">
        {row.item}
        <button onClick={clickEvent => this.delete(index)} className="btn btn-danger float-right">
          <i className="fa fa-trash"></i>
        </button>
      </li>
    );

    return (
      <div className="container mt-8">
        <h3>{this.state.title}</h3>
        <form className="form-inline my-3" autoComplete="off">
          <input className="form-control" type="text"
            name="item"
            placeholder="Type name here"
            onChange={this.handleChange}
            value={this.state.item}
          />
          <button onClick={this.add} className="btn btn-success">
            <i className="fa fa-plus"></i>
          </button>
        </form>
        <ul className="list-group mb-3">
          {rows}
        </ul>
      </div>
    );
  }
}

export default Timeline;
